pub fn S_ISREG(m: u32) bool {
    return m & S_IFMT == S_IFREG;
}

pub fn S_ISDIR(m: u32) bool {
    return m & S_IFMT == S_IFDIR;
}

pub fn S_ISCHR(m: u32) bool {
    return m & S_IFMT == S_IFCHR;
}

pub fn S_ISBLK(m: u32) bool {
    return m & S_IFMT == S_IFBLK;
}

pub fn S_ISFIFO(m: u32) bool {
    return m & S_IFMT == S_IFIFO;
}

pub fn S_ISLNK(m: u32) bool {
    return m & S_IFMT == S_IFLNK;
}

pub fn S_ISSOCK(m: u32) bool {
    return m & S_IFMT == S_IFSOCK;
}

pub fn WEXITSTATUS(s: u32) u32 {
    return (s & 0xff00) >> 8;
}

pub fn WTERMSIG(s: u32) u32 {
    return s & 0x7f;
}

pub fn WSTOPSIG(s: u32) u32 {
    return WEXITSTATUS(s);
}

pub fn WIFEXITED(s: u32) bool {
    return WTERMSIG(s) == 0;
}

pub fn WIFSTOPPED(s: u32) bool {
    return @intCast(u16, ((s & 0xffff) *% 0x10001) >> 8) > 0x7f00;
}

pub fn WIFSIGNALED(s: u32) bool {
    return (s & 0xffff) -% 1 < 0xff;
}

pub fn cap_valid(u8: x) bool {
    return x >= 0 and x <= CAP_LAST_CAP;
}

pub fn CAP_TO_MASK(cap: u8) u32 {
    return @as(u32, 1) << @intCast(u5, cap & 31);
}

pub fn CAP_TO_INDEX(cap: u8) u8 {
    return cap >> 5;
}

const PROT_SEM = switch (builtin.arch) {
    // TODO: also xtensa
    .mips, .mipsel, .mips64, .mips64el => 0x10,
    else => 0x8,
}

const k_sigaction = switch (builtin.arch) {
    .mips, .mipsel => extern struct {
        flags: c_uint,
        handler: ?fn (c_int) callconv(.C) void,
        mask: [4]c_ulong,
        restorer: fn () callconv(.C) void,
    },
    .mips64, .mips64el => extern struct {
        flags: c_uint,
        handler: ?fn (c_int) callconv(.C) void,
        mask: [2]c_ulong,
        restorer: fn () callconv(.C) void,
    },
    else => extern struct {
        handler: ?fn (c_int) callconv(.C) void,
        flags: c_ulong,
        restorer: fn () callconv(.C) void,
        mask: [2]c_uint,
    },
}

const epoll_event = switch (builtin.arch) {
    .x86_64 => packed struct {
        events: u32,
        data: epoll_data,
    },
    else => extern struct {
        events: u32,
        data: epoll_data,
    },
}

const MINSIGSTKSZ = switch (builtin.arch) {
    .i386, .x86_64, .arm, .mipsel => 2048,
    .aarch64 => 5120,
    else => @compileError("MINSIGSTKSZ not defined for this architecture"),
}

const SIGSTKSZ = switch (builtin.arch) {
    .i386, .x86_64, .arm, .mipsel => 8192,
    .aarch64 => 16384,
    else => @compileError("SIGSTKSZ not defined for this architecture"),
}

const stack_t = if (is_mips)
    // IRIX compatible stack_t
    extern struct {
        ss_sp: [*]u8,
        ss_size: usize,
        ss_flags: i32,
    }
else
    extern struct {
        ss_sp: [*]u8,
        ss_flags: i32,
        ss_size: usize,
    }

const siginfo_t = if (is_mips)
    extern struct {
        signo: i32,
        code: i32,
        errno: i32,
        fields: siginfo_fields_union,
    }
else
    extern struct {
        signo: i32,
        errno: i32,
        code: i32,
        fields: siginfo_fields_union,
    }
